const dataText = $("Basic LLM Chain").all()[0]?.json?.text;
const records = dataText.split("-->").filter((record) => record.trim() !== "");
const formattedRecords = records.map((record) => {
  const keyValuePairs = record.split(";;").filter((pair) => pair.trim() !== "");
  const recordObject = keyValuePairs.reduce((acc, pair) => {
    const [key, value] = pair.split("::").map((str) => str.trim());
    acc[key] = value;
    return acc;
  }, {});
  return { json: recordObject };
});
return formattedRecords;
