// Array of motivational topics
const topics = [
  'productivity',
  'mindfulness',
  'success',
  'creativity',
  'resilience',
  'leadership',
  'health',
  'gratitude',
  'innovation',
  'courage'
];

// Generate random index
const randomIndex = Math.floor(Math.random() * topics.length);

// Select random topic
const selectedTopic = topics[randomIndex];

// Get current timestamp
const timestamp = new Date().toISOString();

// Return the result
return [
  {
    json: {
      topic: selectedTopic,
      timestamp: timestamp,
      allTopics: topics
    }
  }
];

// Create success log entry with execution details
const timestamp = new Date().toISOString();
const previousNode = $input.all();

// Extract data from previous nodes
const topic = $('Generate Random Topic').first().json.topic || 'Unknown';
const responseStatus = $('Post to Webhook').first().json.statusCode || 'Unknown';

// Create log entry
const logEntry = {
  status: 'success',
  timestamp: timestamp,
  executionId: $execution.id,
  topic: topic,
  responseStatus: responseStatus,
  itemsProcessed: previousNode.length,
  message: `Successfully executed workflow for topic: ${topic}`
};

// Log to console for debugging
console.log('Execution Success:', logEntry);

// Return summary object
return [{
  json: {
    summary: {
      executionStatus: 'completed',
      timestamp: timestamp,
      topic: topic,
      webhookStatus: responseStatus,
      details: logEntry
    }
  }
}];
